--
CREATE DEFINER = 'root'@'localhost'
FUNCTION FindCaseOfficer(_ApplicationNo Varchar(50))
  RETURNS varchar(50) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci
  DETERMINISTIC
BEGIN
DECLARE TenderID int;
DECLARE TenderValue FLOAT;
DECLARE USER varchar(50);

set TenderID=( SELECT
    TenderID
  FROM applications
  WHERE ApplicationNo = _Applicationno LIMIT 1);
set TenderValue= ( SELECT
    TenderValue
  FROM tenders
  WHERE ID = TenderID LIMIT 1);
set USER= ( SELECT
    Username
  FROM caseofficers
  WHERE MinValue <= TenderValue
  AND `MaxValue` >= TenderValue
  AND NOW() NOT BETWEEN NotAvailableFrom AND NotAvailableTo LIMIT 1);

RETURN USER;

END
