--
-- Create table `requesthandledbuffer`
--
CREATE TABLE requesthandledbuffer (
  Applicationno VARCHAR(100) DEFAULT NULL,
  ApplicationDate DATE DEFAULT NULL,
  Status VARCHAR(50) DEFAULT NULL
)
ENGINE = INNODB,
AVG_ROW_LENGTH = 1260,
CHARACTER SET utf8mb4,
COLLATE utf8mb4_unicode_ci;

DELIMITER $$

--
-- Create procedure `Generaterequesthandled`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Generaterequesthandled(IN _FromDate DATE, IN _ToDate DATE, IN _All BOOLEAN)
BEGIN
  if(_All=1) Then
    Begin
DROP TABLE IF EXISTS requesthandledBuffer;
  create table requesthandledBuffer(Applicationno varchar(100),ApplicationDate Date,Status varchar(50));
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Successful'
  FROM applications
  WHERE ApplicationSuccessful = 1;
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Unsuccessful'
  FROM applications
  WHERE ApplicationSuccessful = 0;
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Withdrawn'
  FROM applications
  WHERE Status = 'WITHDRAWN';
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Pending Determination'
  FROM applications
  WHERE Status <> 'WITHDRAWN'
  AND Status <> 'Closed';
SELECT
  COUNT(Applicationno) AS Count,
  Status
FROM requesthandledBuffer
GROUP BY (Status);
END;
ELSE
      Begin
DROP TABLE IF EXISTS requesthandledBuffer;
  create table requesthandledBuffer(Applicationno varchar(100),ApplicationDate Date,Status varchar(50));
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Successful'
  FROM applications
  WHERE ApplicationSuccessful = 1
  AND FilingDate BETWEEN _FromDate AND DATE_ADD(_ToDate, INTERVAL 1 DAY);
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Unsuccessful'
  FROM applications
  WHERE ApplicationSuccessful = 0
  AND FilingDate BETWEEN _FromDate AND DATE_ADD(_ToDate, INTERVAL 1 DAY);
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Withdrawn'
  FROM applications
  WHERE Status = 'WITHDRAWN'
  AND FilingDate BETWEEN _FromDate AND DATE_ADD(_ToDate, INTERVAL 1 DAY);
INSERT INTO requesthandledBuffer
  SELECT
    ApplicationNo,
    FilingDate,
    'Pending Determination'
  FROM applications
  WHERE Status <> 'WITHDRAWN'
  AND Status <> 'Closed'
  AND FilingDate BETWEEN _FromDate AND DATE_ADD(_ToDate, INTERVAL 1 DAY);
SELECT
  COUNT(Applicationno) AS Count,
  Status
FROM requesthandledBuffer
GROUP BY (Status);
END;
END IF;

END
$$

