--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE AssignCaseOfficer(IN _Applicationno VARCHAR(50), IN _PEID VARCHAR(50), IN _UserID VARCHAR(50))
BEGIN

SELECT
  TenderID
FROM applications
WHERE ApplicationNo = _Applicationno LIMIT 1 INTO @TenderID;
SELECT
  TenderValue
FROM tenders
WHERE ID = @TenderID LIMIT 1 INTO @TenderValue;
DROP TABLE IF EXISTS TempUsers;
CREATE  TABLE TempUsers (
Name VARCHAR(50) NOT NULL
, OngoingCases int NOT NULL 
, CumulativeCases int NOT NULL 
);
INSERT INTO TempUsers (Name, OngoingCases, CumulativeCases)
  SELECT
    Username,
    OngoingCases,
    CumulativeCases
  FROM caseofficers
  WHERE (NOW() NOT BETWEEN NotAvailableFrom AND NotAvailableTo);
SELECT
  Name
FROM TempUsers
WHERE OngoingCases IN (SELECT
    MIN(OngoingCases)
  FROM TempUsers)
OR CumulativeCases IN (SELECT
    MIN(CumulativeCases)
  FROM TempUsers) LIMIT 1 INTO @USER;

INSERT INTO `casedetails` (`UserName`, `ApplicationNo`, `DateAsigned`, `Status`, `PrimaryOfficer`, `Created_At`, `Created_By`, `Deleted`)
  VALUES (@USER, _Applicationno, NOW(), 'Open', 1, NOW(), _UserID, 0);

SELECT
  OngoingCases
FROM caseofficers
WHERE Username = @USER INTO @CurrentOngoingCases;
SELECT
  CumulativeCases
FROM caseofficers
WHERE Username = @USER INTO @CurrentCumulativeCases;
UPDATE caseofficers
SET OngoingCases = @CurrentOngoingCases + 1,
    CumulativeCases = @CurrentCumulativeCases + 1
WHERE Username = @USER;
DELETE
  FROM TempUsers;
END
$$

